mapper.update:
UPDATE order_list
SET ord_status_customer = 's7'
WHERE ord_cd = #{ordCd}

mapper.insert:
INSERT INTO prod_rel ( prod_rel_cd, lot_no, rel_qty, unit_price, rel_total_amount, rel_dt, rel_ord_cd, pcode, prod_ver_cd ) VALUES ( #{prodRelCd}, #{lotNo}, #{relQty},
/* Îã®Í∞Ä: relOrdCd Ïö∞ÏÑ† ‚Üí ÏóÜÏúºÎ©¥ ord_d_cdÎ°ú */ COALESCE( (SELECT od.unit_price FROM order_d od JOIN release_ord ro ON ro.ord_d_cd = od.ord_d_cd WHERE ro.rel_ord_cd = #{relOrdCd}), (SELECT od.unit_price FROM order_d od WHERE od.ord_d_cd = #{ordDCd}) ),
/* Ï¥ùÍ∏àÏï° = Îã®Í∞Ä √ó Ï∂úÍ≥†ÏàòÎüâ */ COALESCE( (SELECT od.unit_price FROM order_d od JOIN release_ord ro ON ro.ord_d_cd = od.ord_d_cd WHERE ro.rel_ord_cd = #{relOrdCd}), (SELECT od.unit_price FROM order_d od WHERE od.ord_d_cd = #{ordDCd}), 0 ) * #{relQty},
SYSTIMESTAMP, #{relOrdCd}, #{pcode}, #{prodVerCd} )

mapper.update:
UPDATE ware_stock ws
   SET ws.qty = ws.qty - #{qty}
 WHERE ws.ware_area_cd = #{wareAreaCd}
   AND ws.prod_inbo_cd IN (
         SELECT pi.prod_inbo_cd
           FROM prod_inbo pi
          WHERE pi.lot_no = #{lotNo}
       )

mapper.select:
SELECT unit_price
  FROM order_d
 WHERE ord_d_cd = #{ord_d_cd}

mapper.update:
UPDATE company
   SET unsettled_amount = NVL(unsettled_amount, 0) + #{amount}
 WHERE cp_cd = #{cpCd}

UPDATE release_ord_master
   SET rel_ord_status = #{status}
 WHERE rel_mas_cd     = #{relMasCd}

UPDATE order_list
   SET ord_status_customer = #{status}
 WHERE ord_cd = #{ordCd}

mapper.select:
SELECT ord_cd
  FROM order_d
 WHERE ord_d_cd = #{ord_d_cd}

mapper.update:
