mapper.select:
SELECT
po.purc_cd, pod.purc_d_cd, mat.mate_name, mat.mate_type, pod.purc_qty, pod.unit, pod.ex_deli_dt, mati.deli_dt, pod.purc_d_status, po.ord_dt, pod.note
po.purc_cd, pod.purc_d_cd, pod.mcode, mat.mate_name, pod.purc_qty, pod.unit, pod.ex_deli_dt, mati.deli_dt, pod.purc_d_status, (pod.purc_qty * pod.unit_price) AS total_amount, pod.note
po.purc_cd, mat.mate_name, pod.purc_qty, po.ord_dt
FROM purc_ord po LEFT JOIN purc_ord_d pod ON po.purc_cd = pod.purc_cd LEFT JOIN material mat ON pod.mcode = mat.mcode LEFT JOIN mate_inbo mati ON pod.purc_d_cd = mati.purc_d_cd
AND UPPER(po.purc_cd) LIKE UPPER('%' || #{purcCd} || '%')
AND UPPER(pod.purc_d_cd) LIKE UPPER('%' || #{purcDCd} || '%')
AND UPPER(mat.mate_name) LIKE UPPER('%' || #{mateName} || '%')
AND pod.mate_type = #{mateType}
AND pod.purc_d_status = #{purcDStatus}
AND pod.mcode = #{mcode}
AND po.ord_dt >= #{startDate}
AND po.ord_dt <= #{endDate}
AND pod.ex_deli_dt >= #{exDeliStartDate}
AND pod.ex_deli_dt <= #{exDeliEndDate}
AND mati.deli_dt >= #{deliStartDate}
AND mati.deli_dt <= #{deliEndDate}
ORDER BY po.ord_dt DESC

SELECT m.mcode, m.mate_ver_cd, m.mate_name, m.mate_type, m.unit, m.std, m.sto_con, m.safe_stock, ms.unit_price, ms.ltime, -- 거래처 정보 c.cp_cd, c.cp_name, c.cp_type, c.repname, c.tel as cp_tel, c.address as cp_address FROM material m INNER JOIN mate_supplier ms ON m.mcode = ms.mcode AND m.mate_ver_cd = ms.mate_ver_cd INNER JOIN company c ON ms.cp_cd = c.cp_cd WHERE m.is_used = 'f1' AND c.is_used = 'f1'
AND m.mcode = #{mcode}
AND UPPER(m.mate_name) LIKE UPPER('%' || #{mateName} || '%')
AND c.cp_cd = #{cpCd}
AND UPPER(c.cp_name) LIKE UPPER('%' || #{cpName} || '%')
ORDER BY m.mate_name, c.cp_name

mapper.update:
UPDATE purc_ord SET
    ord_dt = #{ordDt},
    regi = #{regi},
    purc_status = #{purcStatus},
    ord_total_amount = #{ordTotalAmount}
WHERE purc_cd = #{purcCd}
