mapper.update:
UPDATE move_req 
SET move_status = #{moveStatus},
    appr = #{appr},
    app_dt = CURRENT_TIMESTAMP

        , retu_rea = #{retuRea}

WHERE move_req_cd = #{moveReqCd}

UPDATE move_req 
SET move_status = #{moveStatus},
    appr = #{appr},
    app_dt = #{appDt}

    , note = #{note}

WHERE move_req_cd = #{moveReqCd}

UPDATE move_req 
SET move_status = #{moveStatus},
    appr = #{appr},
    app_dt = #{appDt},
    retu_rea = #{retuRea}
WHERE move_req_cd = #{moveReqCd}

UPDATE ware_stock 
SET qty = qty - #{moveQty}
WHERE 1=1


        AND mate_inbo_cd IN (
            SELECT mate_inbo_cd 
            FROM mate_inbo 
            WHERE mcode = #{mcode} 
            AND mate_ver_cd = #{mateVerCd}
            AND lot_no = #{lotNo}
        )


        AND prod_inbo_cd IN (
            SELECT prod_inbo_cd 
            FROM prod_inbo 
            WHERE pcode = #{pcode} 
            AND prod_ver_cd = #{prodVerCd}
            AND lot_no = #{lotNo}
        )


AND ware_area_cd = #{depaAreaCd}

choose.when:
SELECT COALESCE(SUM(ws.qty), 0) as current_stock
FROM ware_stock ws
INNER JOIN mate_inbo mi ON ws.mate_inbo_cd = mi.mate_inbo_cd
WHERE ws.ware_area_cd = #{wareAreaCd}
AND mi.mcode = #{itemCode}
AND mi.lot_no = #{lotNo}

choose.otherwise:
SELECT COALESCE(SUM(ws.qty), 0) as current_stock
FROM ware_stock ws
INNER JOIN prod_inbo pi ON ws.prod_inbo_cd = pi.prod_inbo_cd
WHERE ws.ware_area_cd = #{wareAreaCd}
AND pi.pcode = #{itemCode}
AND pi.lot_no = #{lotNo}
