mapper.select:
SELECT pp.produ_plan_cd, pp.fcode, pp.fac_ver_cd, pp.plan_dt, pp.plan_start_dt, pp.plan_end_dt, f.fac_name, pp.note, SUM(ppd.plan_qty) AS sum_plan_qty, MIN(ppd.unit) KEEP (DENSE_RANK FIRST ORDER BY ppd.ppdcode) AS first_unit FROM prod_plan pp JOIN factory f ON pp.fcode = f.fcode AND pp.fac_ver_cd = f.fac_ver_cd LEFT JOIN prod_plan_d ppd ON pp.produ_plan_cd = ppd.produ_plan_cd
AND LOWER(pp.produ_plan_cd) LIKE '%' || LOWER(#{produPlanCd}) || '%'
AND pp.plan_dt >= #{planDtStart}
AND pp.plan_dt <= #{planDtEnd}
AND pp.plan_start_dt >= #{periodStartDt}
AND pp.plan_end_dt <= #{periodEndDt}
AND pp.fcode = #{fcode} AND pp.fac_ver_cd = #{facVerCd}
GROUP BY pp.produ_plan_cd, pp.fcode, pp.fac_ver_cd, pp.plan_dt, pp.plan_start_dt, pp.plan_end_dt, f.fac_name, pp.note

mapper.insert:
INSERT INTO prod_plan (
    produ_plan_cd, fcode, fac_ver_cd,
    plan_start_dt, plan_end_dt, mname, plan_dt, note
) VALUES (
    #{produPlanCd}, #{fcode}, #{facVerCd},
    #{planStartDt}, #{planEndDt}, #{mname}, #{planDt}, #{note}
)

INSERT INTO prod_plan_d (
    ppdcode, produ_plan_cd, pcode,
    prod_ver_cd, plan_qty, unit, ex_produ_dt, seq
) VALUES (
    #{ppdcode}, #{produPlanCd}, #{pcode},
    #{prodVerCd}, #{planQty}, #{unit}, #{exProduDt}, #{seq}
)
