mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM MATERIAL
WHERE mcode = #{mcode}

mapper.delete:
DELETE FROM MATE_SUPPLIER
WHERE mcode = #{mcode}
AND mate_ver_cd = #{mateVerCd}

mapper.update:
UPDATE material
SET is_used = #{isUsed},
    modi = #{modi},
    reg_dt = SYSDATE
WHERE mcode = #{mcode}
AND mate_ver_cd = #{mateVerCd}
